mapper.select:
SELECT reply_id, message_type, keyword, template_id, block_flag, create_time
FROM gmair_wechat.auto_reply
WHERE 1 = 1

    AND reply_id = #{replyId}


    AND message_type = #{messageType}


    AND keyword = #{keyword}


    AND template_id = #{templateId}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

mapper.update:
UPDATE gmair_wechat.auto_reply


        message_type = #{messageType},


        keyword = #{keyword},


        block_flag = #{blockFlag}


WHERE template_id = #{templateId}
