mapper.select:
SELECT template_id, picture_url, block_flag, create_time
FROM gmair_wechat.picture_template
WHERE 1 = 1

    AND template_id = #{templateId}


    AND picture_url = #{pictureUrl}


    AND block_flag = #{blockFlag}

ORDER BY create_time DESC

mapper.update:
UPDATE gmair_wechat.picture_template


        picture_url = #{pictureUrl},


        block_flag = #{blockFlag}


WHERE template_id = #{templateId}
